struct Compound: Identifiable, Codable, Hashable {
    enum Class: String, Codable, CaseIterable {
        case testosterone, nandrolone, trenbolone,
             boldenone, drostanolone, stanozolol, metenolone,
             trestolone, dhb

        var displayName: String {
            switch self {
            case .testosterone: return "Testosterone"
            case .nandrolone: return "Nandrolone"
            case .trenbolone: return "Trenbolone"
            case .boldenone: return "Boldenone"
            case .drostanolone: return "Drostanolone (Masteron)"
            case .stanozolol: return "Stanozolol (Winstrol)"
            case .metenolone: return "Metenolone (Primobolan)"
            case .trestolone: return "Trestolone (MENT)"
            case .dhb: return "1-Testosterone (DHB)"
            }
        }
    }

    enum Route: String, Codable, CaseIterable {
        case intramuscular, subcutaneous, oral, transdermal

        var displayName: String {
            switch self {
            case .intramuscular: return "Intramuscular (IM)"
            case .subcutaneous: return "Subcutaneous (SubQ)"
            case .oral: return "Oral"
            case .transdermal: return "Transdermal"
            }
        }
    }

    let id: UUID
    var commonName: String
    var classType: Class
    var ester: String?          // nil for suspensions
    var halfLifeDays: Double
    var defaultBioavailability: [Route: Double]
    var defaultAbsorptionRateKa: [Route: Double] // d-¬π (per day)

    init(id: UUID = UUID(), 
         commonName: String, 
         classType: Class, 
         ester: String? = nil, 
         halfLifeDays: Double, 
         defaultBioavailability: [Route: Double], 
         defaultAbsorptionRateKa: [Route: Double]) {
        self.id = id
        self.commonName = commonName
        self.classType = classType
        self.ester = ester
        self.halfLifeDays = halfLifeDays
        self.defaultBioavailability = defaultBioavailability
        self.defaultAbsorptionRateKa = defaultAbsorptionRateKa
    }

    // Returns the full display name with class and ester, e.g. "Testosterone Enanthate"
    var fullDisplayName: String {
        if let ester = ester {
            return "\(classType.displayName) \(ester)"
        } else {
            return "\(classType.displayName) Suspension"
        }
    }
}import Foundation

class CompoundLibrary: ObservableObject {
    @Published private(set) var compounds: [Compound] = []
    @Published private(set) var blends: [VialBlend] = []

    init() {
        populateCompounds()
        populateBlends()
    }

    // MARK: - Helper Methods

    func compound(withID id: UUID) -> Compound? {
        return compounds.first { $0.id == id }
    }

    func blend(withID id: UUID) -> VialBlend? {
        return blends.first { $0.id == id }
    }

    // MARK: - Filter Methods

    func compounds(ofClass classType: Compound.Class) -> [Compound] {
        return compounds.filter { $0.classType == classType }
    }

    func compounds(forRoute route: Compound.Route) -> [Compound] {
        return compounds.filter { $0.defaultBioavailability[route] != nil }
    }

    func compounds(withEsterName esterName: String) -> [Compound] {
        return compounds.filter { $0.ester == esterName }
    }

    func compounds(withHalfLifeBetween min: Double, and max: Double) -> [Compound] {
        return compounds.filter { $0.halfLifeDays >= min && $0.halfLifeDays <= max }
    }

    func blends(containing compoundID: UUID) -> [VialBlend] {
        return blends.filter { blend in
            blend.components.contains { $0.compoundID == compoundID }
        }
    }

    // MARK: - Populate Data

    private func populateCompounds() {
        // Default values for typical routes
        let defaultIMBioavailability: [Compound.Route: Double] = [.intramuscular: 1.0, .subcutaneous: 0.85]
        let defaultOralBioavailability: [Compound.Route: Double] = [.oral: 0.07] // Low for most oral testosterone

        // Testosterone compounds
        let testosteronePropionate = Compound(
            commonName: "Testosterone Propionate",
            classType: .testosterone,
