import CoreData
import CloudKit

class CoreDataManager {
    static let shared = CoreDataManager()

    // MARK: - Core Data stack

    lazy var persistentContainer: NSPersistentContainer = {
        // Set up the CloudKit container
        let cloudKitContainerID = "iCloud.flight505.TestoSim"

        // Check if iCloud sync is enabled via UserDefaults
        let usesICloudSync = UserDefaults.standard.bool(forKey: "usesICloudSync")

        // Use NSPersistentCloudKitContainer if iCloud sync is enabled, otherwise use regular NSPersistentContainer
        let container: NSPersistentContainer

        if usesICloudSync {
            container = NSPersistentCloudKitContainer(name: "TestoSim")
            print("Using CloudKit-enabled persistent container")

            // Configure CloudKit integration
            if let cloudStoreDescription = container.persistentStoreDescriptions.first {
                // Enable CloudKit
                cloudStoreDescription.cloudKitContainerOptions = NSPersistentCloudKitContainerOptions(containerIdentifier: cloudKitContainerID)

                // Enable history tracking (required for CloudKit sync)
                cloudStoreDescription.setOption(true as NSNumber, forKey: NSPersistentHistoryTrackingKey)
                cloudStoreDescription.setOption(true as NSNumber, forKey: NSPersistentStoreRemoteChangeNotificationPostOptionKey)
            }
        } else {
            container = NSPersistentContainer(name: "TestoSim")
            print("Using standard persistent container (CloudKit disabled)")
        }

        // Initialize the Core Data stack
        container.loadPersistentStores(completionHandler: { (storeDescription, error) in
            if let error = error as NSError? {
                // Replace this implementation with code to handle the error appropriately.
                print("Unresolved error loading persistent stores: \(error), \(error.userInfo)")

                // Log CloudKit specific issues if present
                if let cloudError = error.userInfo[NSUnderlyingErrorKey] as? NSError,
                   cloudError.domain == CKErrorDomain {
                    print("CloudKit error: \(cloudError.localizedDescription)")
                }
            } else {
                print("Successfully loaded persistent store: \(storeDescription)")

                // Initialize CloudKit schema if CloudKit is enabled
                if let cloudKitOptions = storeDescription.cloudKitContainerOptions,
                   let cloudKitContainer = container as? NSPersistentCloudKitContainer {
                    do {
                        try cloudKitContainer.initializeCloudKitSchema(options: [.printSchema])
                        print("CloudKit schema initialized successfully")
                    } catch {
                        print("Error initializing CloudKit schema: \(error)")
                    }
                }
            }
        })

        // Enable automatic merging of changes from the parent context
        container.viewContext.automaticallyMergesChangesFromParent = true
        container.viewContext.mergePolicy = NSMergeByPropertyObjectTrumpMergePolicy

        return container
    }()

    // MARK: - Core Data operations

    func saveContext() {
        let context = persistentContainer.viewContext
        if context.hasChanges {
            do {
                try context.save()
                print("Context saved successfully")
            } catch {
                let nserror = error as NSError
                print("Unresolved error saving context: \(nserror), \(nserror.userInfo)")
            }
        }
    }

    // MARK: - Background task

    func performBackgroundTask(_ block: @escaping (NSManagedObjectContext) -> Void) {
        persistentContainer.performBackgroundTask(block)
    }

    // MARK: - iCloud Sync Control

    func enableCloudSync(_ enable: Bool) {
        let currentSetting = UserDefaults.standard.bool(forKey: "usesICloudSync")

        // Only update if the setting has changed
        if currentSetting != enable {
            UserDefaults.standard.set(enable, forKey: "usesICloudSync")

            // Notify the user that a restart is required
            let notification = Notification(name: Notification.Name("CloudKitSyncSettingChanged"), 
                                           object: nil, 
                                           userInfo: ["enabled": enable])
            NotificationCenter.default.post(notification)

            print("CloudKit sync \(enable ? "enabled" : "disabled") - app restart required for changes to take effect")
        }
    }

    func isCloudSyncEnabled() -> Bool {
        return UserDefaults.standard.bool(forKey: "usesICloudSync")
    }

    // MARK: - Migration

    func migrateUserProfileFromJSON() {
        // Check if we've already migrated
        if UserDefaults.standard.bool(forKey: "migrated") {
            return
        }

        // Get the profile data from UserDefaults
        guard let profileData = UserDefaults.standard.data(forKey: "userProfileData") else {
            // No data to migrate
            UserDefaults.standard.set(true, forKey: "migrated")
            return
        }

        let decoder = JSONDecoder()

        do {
            // Decode the JSON data into our model struct
            let profile = try decoder.decode(UserProfile.self, from: profileData)

            // Create Core Data versions of the entities
            let context = persistentContainer.viewContext

            // Create CDUserProfile
            let cdProfile = CDUserProfile(context: context)
            cdProfile.id = profile.id
            cdProfile.name = profile.name
            cdProfile.unit = profile.unit
            cdProfile.calibrationFactor = profile.calibrationFactor
            cdProfile.dateOfBirth = profile.dateOfBirth
            cdProfile.heightCm = profile.heightCm ?? 0
            cdProfile.weight = profile.weight ?? 0
            cdProfile.biologicalSex = profile.biologicalSex.rawValue
            cdProfile.usesICloudSync = profile.usesICloudSync

            // Create CDInjectionProtocol entries
            for p in profile.protocols {
                let cdProtocol = CDInjectionProtocol(context: context)
                cdProtocol.id = p.id
                cdProtocol.name = p.name
                cdProtocol.doseMg = p.doseMg
                cdProtocol.frequencyDays = p.frequencyDays
                cdProtocol.startDate = p.startDate
                cdProtocol.notes = p.notes

                // TODO: The Core Data model needs to be updated to include these properties:
                // - compoundID (UUID)
                // - blendID (UUID)
                // - selectedRoute (String)
                // Once added, uncomment the following lines:
                /*
                cdProtocol.compoundID = p.compoundID
                cdProtocol.blendID = p.blendID
                cdProtocol.selectedRoute = p.selectedRoute
                */

                // For now, store this information in the notes field
                var extendedInfo = ""
                if let compoundID = p.compoundID {
                    extendedInfo += "CompoundID: \(compoundID.uuidString)\n"
                }
                if let blendID = p.blendID {
                    extendedInfo += "BlendID: \(blendID.uuidString)\n"
                }
                if let route = p.selectedRoute {
                    extendedInfo += "Route: \(route)\n"
                }

                if !extendedInfo.isEmpty {
                    if cdProtocol.notes != nil {
                        cdProtocol.notes = cdProtocol.notes! + "\n\n---EXTENDED_DATA---\n" + extendedInfo
                    } else {
                        cdProtocol.notes = "---EXTENDED_DATA---\n" + extendedInfo
                    }
                }

                // Create blood samples
                for sample in p.bloodSamples {
                    let cdSample = CDBloodSample(context: context)
                    cdSample.id = sample.id
                    cdSample.date = sample.date
                    cdSample.value = sample.value
                    cdSample.unit = sample.unit

                    cdProtocol.addToBloodSamples(cdSample)
                }

                cdProfile.addToProtocols(cdProtocol)
            }

            // Save to Core Data
            try context.save()

            // Mark as migrated
            UserDefaults.standard.set(true, forKey: "migrated")
            print("Successfully migrated user profile from JSON to Core Data")

        } catch {
            print("Error migrating from JSON: \(error)")
        }
    }
}import SwiftUI

/// View for displaying AI-generated insights
struct AIInsightsView: View {
    @EnvironmentObject var dataStore: AppDataStore
    @StateObject private var insightsGenerator = AIInsightsGenerator()
    @ObservedObject private var openAIService = OpenAIService.shared

    var protocolID: UUID?
    var cycleID: UUID?

    @State private var isLoading = false
    @State private var expandedPoints: Set<String> = []
    @State private var showSettings = false
    @State private var errorMessage: String?

    var body: some View {
        ScrollView {
            VStack(alignment: .leading, spacing: 16) {
                // Test API key indicator
                if openAIService.isUsingTestKey {
                    testKeyIndicator
                }

                // API key warning if no key is set
                if !OpenAIService.shared.hasAPIKey() {
                    noApiKeyView
                }

                // Error message if present
                if let message = errorMessage {
                    errorView(message)
                }

                if isLoading {
                    loadingView
                } else if let insights = insightsGenerator.latestInsights {
                    insightsContent(insights)
                } else {
                    noInsightsView
                }
            }
            .padding()
        }
        .navigationTitle("AI Insights")
        .toolbar {
            ToolbarItem(placement: .primaryAction) {
                Button {
                    generateInsights(forceRefresh: true)
                } label: {
                    Label("Refresh", systemImage: "arrow.clockwise")
                }
                .disabled(isLoading)
            }

            ToolbarItem(placement: .topBarTrailing) {
                Button {
                    showSettings = true
                } label: {
                    Label("Settings", systemImage: "gear")
                }
            }
        }
        .onAppear {
            generateInsights()
            // Set up error handling monitoring
            updateErrorMessage()
        }
        .sheet(isPresented: $showSettings) {
            AISettingsView(insightsGenerator: insightsGenerator)
        }
        // Use a task to monitor error state changes instead of onChange
        .task {
            for await _ in insightsGenerator.$error.values {
                updateErrorMessage()
            }
        }
    }

    // MARK: - Private Views

    private var testKeyIndicator: some View {
        HStack {
            Image(systemName: "checkmark.seal.fill")
                .foregroundColor(.green)
            Text("Using Test API Key")
                .font(.caption)
                .foregroundColor(.green)
            Spacer()
        }
        .padding(.horizontal, 10)
        .padding(.vertical, 5)
        .background(Color.green.opacity(0.1))
        .cornerRadius(5)
    }

    private var noApiKeyView: some View {
        VStack(alignment: .leading, spacing: 10) {
            HStack {
                Image(systemName: "key.slash")
                    .foregroundColor(.yellow)
                Text("OpenAI API Key Required")
                    .font(.headline)
                    .foregroundColor(.yellow)
            }

            Text("To use AI-powered insights, you need to set up your OpenAI API key in settings.")
                .font(.subheadline)

            Button("Set API Key") {
                showSettings = true
            }
            .buttonStyle(.borderedProminent)
            .padding(.top, 4)
        }
        .padding()
        .background(Color.yellow.opacity(0.1))
        .cornerRadius(10)
    }

    private func errorView(_ message: String) -> some View {
        VStack(alignment: .leading, spacing: 10) {
            HStack {
                Image(systemName: "exclamationmark.triangle")
                    .foregroundColor(.red)
                Text("Error")
                    .font(.headline)
                    .foregroundColor(.red)
            }

            Text(message)
                .font(.subheadline)

            Button("Try Again") {
                errorMessage = nil
                generateInsights(forceRefresh: true)
            }
            .buttonStyle(.borderedProminent)
            .padding(.top, 4)
        }
        .padding()
        .background(Color.red.opacity(0.1))
        .cornerRadius(10)
    }

    private var loadingView: some View {
        VStack(spacing: 20) {
            ProgressView()
                .scaleEffect(1.5)

            Text("Analyzing data and generating insights...")
                .font(.headline)

            Text("This may take a moment")
                .foregroundColor(.secondary)
        }
        .frame(maxWidth: .infinity, minHeight: 300)
    }

    private var noInsightsView: some View {
        VStack(spacing: 20) {
            Image(systemName: "lightbulb")
                .font(.system(size: 40))
                .foregroundColor(.orange)

            if dataStore.selectedProtocolID == nil && dataStore.selectedCycleID == nil {
                Text("No protocol or cycle selected")
                    .font(.headline)

                Text("Select a protocol or cycle from the Protocols or Cycles tab to generate insights.")
                    .multilineTextAlignment(.center)
                    .foregroundColor(.secondary)
            } else {
                Text("No insights available")
                    .font(.headline)

                Text("Tap the refresh button to generate insights based on your current protocol or cycle data.")
                    .multilineTextAlignment(.center)
                    .foregroundColor(.secondary)
            }

            Button("Generate Insights") {
                generateInsights(forceRefresh: true)
            }
            .buttonStyle(.borderedProminent)
            .padding(.top)
        }
        .frame(maxWidth: .infinity, minHeight: 300)
        .padding()
    }

    private func insightsContent(_ insights: Insights) -> some View {
        VStack(alignment: .leading, spacing: 16) {
            // Title
            Text(insights.title)
                .font(.title)
                .fontWeight(.bold)

            // Summary
            Text(insights.summary)
                .font(.headline)
                .padding(.bottom, 8)

            // Disclaimer
            Text("Note: These insights are generated by AI and should not replace medical advice.")
                .font(.caption)
                .foregroundColor(.secondary)
                .padding(.bottom, 8)

            // Blend explanation if available
            if let blendExplanation = insights.blendExplanation {
                blendExplanationView(blendExplanation)
            }

            // Key points
            if !insights.keyPoints.isEmpty {
                Text("Key Points")
                    .font(.title2)
                    .fontWeight(.bold)
                    .padding(.top, 8)

                ForEach(insights.keyPoints, id: \.title) { point in
                    keyPointView(point)
                }
            }
        }
    }

    private func blendExplanationView(_ explanation: String) -> some View {
        VStack(alignment: .leading, spacing: 10) {
            Text("Blend Analysis")
                .font(.title2)
                .fontWeight(.bold)

            Text(explanation)
                .font(.body)
                .lineSpacing(4)
        }
        .padding()
        .background(Color.blue.opacity(0.1))
        .cornerRadius(10)
    }

    private func keyPointView(_ point: KeyPoint) -> some View {
        let pointId = point.title
        let isExpanded = expandedPoints.contains(pointId)

        return VStack(alignment: .leading, spacing: 8) {
            // Header row with icon, title, and expand/collapse button
            HStack {
                iconForKeyPoint(point.type)
                    .font(.headline)
                    .foregroundColor(colorForKeyPoint(point.type))
                    .frame(width: 24, height: 24)

                Text(point.title)
                    .font(.headline)
                    .foregroundColor(colorForKeyPoint(point.type))

                Spacer()

                Button {
                    withAnimation {
                        if isExpanded {
                            expandedPoints.remove(pointId)
                        } else {
                            expandedPoints.insert(pointId)
                        }
                    }
                } label: {
                    Image(systemName: isExpanded ? "chevron.up" : "chevron.down")
                        .foregroundColor(.secondary)
                }
            }

            // Description (visible when expanded)
            if isExpanded {
                Text(point.description)
                    .font(.body)
                    .foregroundColor(.primary)
                    .padding(.leading, 32)
                    .padding(.top, 4)
            }
        }
        .padding()
        .background(backgroundForKeyPoint(point.type).opacity(0.1))
        .cornerRadius(10)
        .contentShape(Rectangle()) // Make the entire row tappable
        .onTapGesture {
            withAnimation {
                if isExpanded {
                    expandedPoints.remove(pointId)
                } else {
                    expandedPoints.insert(pointId)
                }
            }
        }
    }

    // MARK: - Helper Methods

    private func generateInsights(forceRefresh: Bool = false) {
        self.isLoading = true

        if let protocolID = protocolID, let treatmentProtocol = dataStore.profile.protocols.first(where: { $0.id == protocolID }) {
            // Generate insights for a specific protocol
            insightsGenerator.generateInsights(
                for: treatmentProtocol,
                profile: dataStore.profile,
                simulationData: dataStore.simulationData,
                compoundLibrary: dataStore.compoundLibrary,
                forceRefresh: forceRefresh
            )
        } else if let cycleID = cycleID, let cycle = dataStore.cycles.first(where: { $0.id == cycleID }) {
            // Generate insights for a specific cycle
            insightsGenerator.generateCycleInsights(
                for: cycle,
                profile: dataStore.profile,
                simulationData: dataStore.cycleSimulationData,
                compoundLibrary: dataStore.compoundLibrary,
                forceRefresh: forceRefresh
            )
        } else if let selectedProtocolID = dataStore.selectedProtocolID,
                  let selectedProtocol = dataStore.profile.protocols.first(where: { $0.id == selectedProtocolID }) {
            // Use the selected protocol as fallback
            insightsGenerator.generateInsights(
                for: selectedProtocol,
                profile: dataStore.profile,
                simulationData: dataStore.simulationData,
                compoundLibrary: dataStore.compoundLibrary,
                forceRefresh: forceRefresh
            )
        } else if let selectedCycleID = dataStore.selectedCycleID,
                  let selectedCycle = dataStore.cycles.first(where: { $0.id == selectedCycleID }) {
            // Use the selected cycle as fallback
            insightsGenerator.generateCycleInsights(
                for: selectedCycle,
                profile: dataStore.profile,
                simulationData: dataStore.cycleSimulationData,
                compoundLibrary: dataStore.compoundLibrary,
                forceRefresh: forceRefresh
            )
        } else {
            // If no protocol or cycle is found, show no insights available
            self.isLoading = false
            return
        }

        // Update loading state based on the generator's state
        DispatchQueue.main.async {
            self.isLoading = self.insightsGenerator.isLoading
        }
    }

    private func iconForKeyPoint(_ type: KeyPoint.KeyPointType) -> some View {
        switch type {
        case .information:
            return Image(systemName: "info.circle.fill")
        case .positive:
            return Image(systemName: "checkmark.circle.fill")
        case .warning:
            return Image(systemName: "exclamationmark.triangle.fill")
        case .suggestion:
            return Image(systemName: "lightbulb.fill")
        }
    }

    private func colorForKeyPoint(_ type: KeyPoint.KeyPointType) -> Color {
        switch type {
        case .information:
            return .blue
        case .positive:
            return .green
        case .warning:
            return .orange
        case .suggestion:
            return .purple
        }
    }

    private func backgroundForKeyPoint(_ type: KeyPoint.KeyPointType) -> Color {
        switch type {
        case .information:
            return .blue
        case .positive:
            return .green
        case .warning:
            return .orange
        case .suggestion:
            return .purple
        }
    }

    private func updateErrorMessage() {
        if let error = insightsGenerator.error {
            errorMessage = error.localizedDescription
        } else {
            errorMessage = nil
        }
    }
}

// MARK: - AISettingsView

/// View for managing AI settings, including API key
struct AISettingsView: View {
    @Environment(\.dismiss) private var dismiss
    @State private var apiKey: String = UserDefaults.standard.string(forKey: "openai_api_key") ?? ""
    @State private var showingSuccessMessage = false
    @State private var useTestKey: Bool = UserDefaults.standard.bool(forKey: "use_test_api_key")
    @EnvironmentObject var dataStore: AppDataStore

    // Reference to the shared insights generator
    private let insightsGenerator: AIInsightsGenerator

    init(insightsGenerator: AIInsightsGenerator = AIInsightsGenerator()) {
        self.insightsGenerator = insightsGenerator
    }

    var body: some View {
        NavigationStack {
            Form {
                Section(header: Text("API Key Options")) {
                    Toggle("Use Free Test API Key", isOn: $useTestKey)
                        .onChange(of: useTestKey) { oldValue, newValue in
                            OpenAIService.shared.toggleTestApiKey(newValue)
                            insightsGenerator.refreshAfterAPIKeyChange()
                        }

                    if useTestKey {
                        Text("Using the free test API key with a $20 spending limit for all test users.")
                            .font(.caption)
                            .foregroundColor(.green)
                    } else {
                        Text("Using your personal API key.")
                            .font(.caption)
                            .foregroundColor(.secondary)
                    }
                }

                if !useTestKey {
                    Section(header: Text("Personal OpenAI API Key")) {
                        SecureField("Enter API Key", text: $apiKey)
                            .autocorrectionDisabled()
                            .textInputAutocapitalization(.never)

                        Text("Your API key is stored securely in your device's UserDefaults and is never shared.")
                            .font(.caption)
                            .foregroundColor(.secondary)
                    }

                    Section {
                        Button("Save API Key") {
                            OpenAIService.shared.saveAPIKey(apiKey)
                            insightsGenerator.refreshAfterAPIKeyChange()
                            showingSuccessMessage = true
                        }
                        .disabled(apiKey.isEmpty)

                        if !apiKey.isEmpty {
                            Button("Clear API Key") {
                                apiKey = ""
                                OpenAIService.shared.clearAPIKey()
                                insightsGenerator.refreshAfterAPIKeyChange()
                                showingSuccessMessage = true
                            }
                            .foregroundColor(.red)
                        }
                    }
                }

                Section(header: Text("About AI Insights")) {
                    VStack(alignment: .leading, spacing: 8) {
                        Text("How it works")
                            .font(.headline)

                        Text("The AI Insights feature uses OpenAI's API to analyze your hormone protocols and cycles. It provides personalized feedback, optimization suggestions, and educational content based on your specific therapy details.")
                            .font(.caption)
                            .foregroundColor(.secondary)
                    }
                    .padding(.vertical, 8)

                    VStack(alignment: .leading, spacing: 8) {
                        Text("Privacy")
                            .font(.headline)

                        Text("Only anonymized therapy data is sent to OpenAI for analysis. No personally identifiable information is shared. All API calls are made directly from your device.")
                            .font(.caption)
                            .foregroundColor(.secondary)
                    }
                    .padding(.vertical, 8)

                    VStack(alignment: .leading, spacing: 8) {
                        Text("Test API Key")
                            .font(.headline)

                        Text("A free test API key is provided for evaluation purposes with a $20 spending limit across all users. For continued use after testing, we recommend using your own API key.")
                            .font(.caption)
                            .foregroundColor(.secondary)
                    }
                    .padding(.vertical, 8)
                }
            }
            .navigationTitle("AI Settings")
            .navigationBarTitleDisplayMode(.inline)
            .toolbar {
                ToolbarItem(placement: .confirmationAction) {
                    Button("Done") {
                        dismiss()
                    }
                }
            }
            .alert("Settings Saved", isPresented: $showingSuccessMessage) {
                Button("OK") { }
            } message: {
                Text(apiKey.isEmpty ? 
                     "API key has been cleared. The app will use mock data for insights." : 
                     "API key has been saved. The app will now use OpenAI for generating insights.")
            }
        }
    }
}

