struct CycleDetailView: View {
    let cycle: Cycle
    @EnvironmentObject var dataStore: AppDataStore
    @State private var isPresentingEditForm = false
    @State private var isPresentingStageForm = false

    var body: some View {
        NavigationView {
            ScrollView {
                VStack(alignment: .leading, spacing: 16) {
                    // Cycle information
                    VStack(alignment: .leading, spacing: 8) {
                        Text(cycle.name)
                            .font(.title)
                            .bold()

                        HStack {
                            Text("Start: \(formatDate(cycle.startDate))")
                            Spacer()
                            Text("End: \(formatDate(cycle.endDate))")
                        }
                        .font(.subheadline)
                        .foregroundColor(.secondary)

                        if let notes = cycle.notes, !notes.isEmpty {
                            Text(notes)
                                .font(.body)
                                .padding(.top, 4)
                        }
                    }
                    .padding()
                    .background(
                        RoundedRectangle(cornerRadius: 12)
                            .fill(Color(.secondarySystemBackground))
                    )
                    .padding(.horizontal)

                    // Stages Timeline
                    if cycle.stages.isEmpty {
                        emptyStagesView
                    } else {
                        stagesTimelineView
                    }

                    // Simulation Results
                    if dataStore.isCycleSimulationActive, !dataStore.cycleSimulationData.isEmpty {
                        VStack(alignment: .leading, spacing: 8) {
                            Text("Simulation Results")
                                .font(.headline)
                                .padding(.bottom, 5)

                            CycleChartView(simulationData: dataStore.cycleSimulationData)
                                .frame(height: 200)
                        }
                        .padding()
                        .background(
                            RoundedRectangle(cornerRadius: 12)
                                .fill(Color(.secondarySystemBackground))
                        )
                        .padding(.horizontal)
                    }

                    Spacer()
                }
                .padding(.vertical)
            }
            .navigationBarTitleDisplayMode(.inline)
            .toolbar {
                ToolbarItem(placement: .primaryAction) {
                    Button(action: {
                        isPresentingEditForm = true
                    }) {
                        Text("Edit")
                    }
                }

                ToolbarItem(placement: .primaryAction) {
                    Button(action: {
                        isPresentingStageForm = true
                    }) {
                        Label("Add Stage", systemImage: "plus")
                    }
                }
            }
            .sheet(isPresented: $isPresentingStageForm) {
                CycleStageFormView(isPresented: $isPresentingStageForm, cycle: cycle)
                    .environmentObject(dataStore)
            }
            .sheet(isPresented: $isPresentingEditForm) {
                // TODO: Add edit cycle form here
                Text("Edit Cycle")
            }
        }
    }

    private var emptyStagesView: some View {
        VStack(spacing: 16) {
            Image(systemName: "calendar.badge.plus")
                .font(.system(size: 40))
                .foregroundColor(.secondary)

            Text("No Stages Yet")
                .font(.headline)

            Text("Add stages to build your cycle timeline")
                .font(.subheadline)
                .multilineTextAlignment(.center)
                .foregroundColor(.secondary)

            Button(action: {
                isPresentingStageForm = true
            }) {
                Text("Add Stage")
                    .font(.headline)
                    .padding()
                    .background(Color.accentColor)
                    .foregroundColor(.white)
                    .cornerRadius(10)
            }
        }
        .padding()
        .frame(maxWidth: .infinity)
        .background(
            RoundedRectangle(cornerRadius: 12)
                .fill(Color(.secondarySystemBackground))
        )
        .padding(.horizontal)
    }

    private var stagesTimelineView: some View {
        VStack(alignment: .leading, spacing: 8) {
            Text("Timeline")
                .font(.headline)
                .padding(.bottom, 5)

            // Timeline visualization will go here
            Text("Timeline visualization placeholder")
                .frame(height: 100)
                .frame(maxWidth: .infinity)
                .background(Color.gray.opacity(0.1))
                .cornerRadius(8)
        }
        .padding()
        .background(
            RoundedRectangle(cornerRadius: 12)
                .fill(Color(.secondarySystemBackground))
        )
        .padding(.horizontal)
    }

    private func formatDate(_ date: Date) -> String {
        let formatter = DateFormatter()
        formatter.dateStyle = .medium
        formatter.timeStyle = .none
        return formatter.string(from: date)
    }
}

