131:         Button(action: action) {
132:             Text(title)
133:                 .padding(.vertical, 8)
134:                 .padding(.horizontal, 16)
135:                 .background(isSelected ? Color.green : Color(.systemGray5))
136:                 .foregroundColor(isSelected ? .white : .primary)
137:                 .cornerRadius(20)
138:         }
139:     }
140: }
141: struct CompoundRow: View {
