 3: # Usage: ./build-test.sh [clean]
 4: set -o pipefail
 5: # Default simulator device
 6: DEVICE="iPhone 16,OS=18.4"
 7: # Check if xcbeautify is installed
 8: if ! command -v xcbeautify &> /dev/null; then
 9:     echo "xcbeautify not found. Installing..."
10:     brew install xcbeautify
11: fi
12: # Check if we need to clean
13: if [ "$1" == "clean" ]; then
14:     echo "Cleaning build..."
15:     xcodebuild -project TestoSim.xcodeproj -scheme TestoSim -destination "platform=iOS Simulator,name=$DEVICE" clean | xcbeautify
16: fi
17: # Build only, don't run
18: echo "Building TestoSim..."
19: xcodebuild -project TestoSim.xcodeproj -scheme TestoSim -destination "platform=iOS Simulator,name=$DEVICE" build | xcbeautify
20: # Check build status
21: if [ $? -eq 0 ]; then
22:     echo "‚úÖ Build succeeded. Use SweetPad to run in simulator."
23:     echo ""
24:     echo "To run in SweetPad:"
25:     echo "1. Open VS Code with SweetPad extension"
26:     echo "2. Run task 'SweetPad: Build' from command palette or context menu"
27:     echo ""
28:     echo "This avoids launching multiple simulators."
29: else
30:     echo "‚ùå Build failed."
31: fi

