 47:     var chartSection: some View {
 48:         VStack(alignment: .leading, spacing: 8) {
 49:             Text("Calibration Results")
 50:                 .font(.headline)
 51:             Text("The chart shows the original and calibrated testosterone predictions alongside your blood samples.")
 52:                 .font(.caption)
 53:                 .foregroundColor(.secondary)
 54:             Chart {
