281:         .padding(.vertical, 4)
282:     }
283:     private func formatFrequency(_ days: Double) -> String {
284:         if days == 1 {
285:             return "day"
286:         } else if days == 7 {
287:             return "week"
288:         } else if days == 3.5 {
