175:     /// Creates mock insights for a protocol
176:     private func createMockInsightsForProtocol(
177:         _ treatmentProtocol: InjectionProtocol,
178:         profile: UserProfile,
179:         simulationData: [DataPoint],
180:         compoundLibrary: CompoundLibrary
181:     ) -> Insights {
182:         // Extract protocol details
183:         let protocolType = treatmentProtocol.protocolType
184:         let protocolName = treatmentProtocol.name
185:         var compoundOrBlendName = "Unknown"
186:         // Determine the compound or blend name
187:         if protocolType == .compound, let compoundID = treatmentProtocol.compoundID,
188:            let compound = compoundLibrary.compounds.first(where: { $0.id == compoundID }) {
189:             compoundOrBlendName = compound.commonName
190:             if let ester = compound.ester {
191:                 compoundOrBlendName += " \(ester)"
192:             }
193:         } else if protocolType == .blend, let blendID = treatmentProtocol.blendID,
194:                   let blend = compoundLibrary.blends.first(where: { $0.id == blendID }) {
195:             compoundOrBlendName = blend.name
196:         }
197:         // Get simulation statistics
198:         let maxLevel = simulationData.map { $0.level }.max() ?? 0
199:         let minLevel = simulationData.map { $0.level }.min() ?? 0
200:         let avgLevel = simulationData.map { $0.level }.reduce(0, +) / Double(max(1, simulationData.count))
201:         let fluctuation = maxLevel > 0 ? (maxLevel - minLevel) / maxLevel * 100 : 0
202:         // Generate insights based on protocol characteristics
203:         var insights = Insights(
204:             title: "Insights for \(protocolName)",
205:             summary: "Analysis of your \(compoundOrBlendName) protocol.",
206:             keyPoints: []
207:         )
208:         // Add blend explanation if it's a blend
209:         if protocolType == .blend {
210:             insights.blendExplanation = createMockBlendExplanation(treatmentProtocol, compoundLibrary: compoundLibrary)
211:         }
212:         // Add key points based on protocol characteristics
213:         // 1. Frequency point
214:         if treatmentProtocol.frequencyDays >= 7 {
215:             insights.keyPoints.append(
216:                 KeyPoint(
217:                     title: "Consider splitting your dose",
218:                     description: "Your current injection frequency of every \(treatmentProtocol.frequencyDays) days leads to significant hormone fluctuations. Consider splitting your total dose into smaller, more frequent injections to achieve more stable hormone levels.",
219:                     type: .suggestion
220:                 )
221:             )
222:         } else if treatmentProtocol.frequencyDays <= 2 {
223:             insights.keyPoints.append(
224:                 KeyPoint(
225:                     title: "Good injection frequency",
226:                     description: "Your frequent injection schedule of every \(treatmentProtocol.frequencyDays) days helps maintain stable hormone levels with minimal fluctuations.",
227:                     type: .positive
228:                 )
229:             )
230:         }
231:         // 2. Fluctuation point
232:         if fluctuation > 40 {
233:             insights.keyPoints.append(
234:                 KeyPoint(
235:                     title: "High level fluctuation",
236:                     description: "Your current protocol results in approximately \(fluctuation.isFinite ? Int(fluctuation) : 0)% fluctuation between peak and trough levels, which may lead to inconsistent symptoms and effects.",
237:                     type: .warning
238:                 )
239:             )
240:         } else if fluctuation < 20 {
241:             insights.keyPoints.append(
242:                 KeyPoint(
243:                     title: "Stable hormone levels",
244:                     description: "Your protocol achieves excellent stability with only \(fluctuation.isFinite ? Int(fluctuation) : 0)% fluctuation between peak and trough levels.",
245:                     type: .positive
246:                 )
247:             )
248:         }
249:         // 3. Overall level point
250:         let targetMin = 400.0 // Example target minimum
251:         let targetMax = 1000.0 // Example target maximum
252:         if avgLevel < targetMin {
253:             insights.keyPoints.append(
254:                 KeyPoint(
255:                     title: "Levels below typical target range",
256:                     description: "Your average level of \(avgLevel.isFinite ? Int(avgLevel) : 0) ng/dL is below the typical target range of \(targetMin.isFinite ? Int(targetMin) : 0)-\(targetMax.isFinite ? Int(targetMax) : 0) ng/dL. Consider discussing a dosage adjustment with your healthcare provider.",
257:                     type: .warning
258:                 )
259:             )
260:         } else if avgLevel > targetMax {
261:             insights.keyPoints.append(
262:                 KeyPoint(
263:                     title: "Levels above typical target range",
264:                     description: "Your average level of \(avgLevel.isFinite ? Int(avgLevel) : 0) ng/dL is above the typical target range of \(targetMin.isFinite ? Int(targetMin) : 0)-\(targetMax.isFinite ? Int(targetMax) : 0) ng/dL. Consider discussing potential side effects and benefits with your healthcare provider.",
265:                     type: .warning
266:                 )
267:             )
268:         } else {
269:             insights.keyPoints.append(
270:                 KeyPoint(
271:                     title: "Levels within typical target range",
272:                     description: "Your average level of \(avgLevel.isFinite ? Int(avgLevel) : 0) ng/dL falls within the typical target range of \(targetMin.isFinite ? Int(targetMin) : 0)-\(targetMax.isFinite ? Int(targetMax) : 0) ng/dL.",
273:                     type: .positive
274:                 )
275:             )
276:         }
277:         return insights
278:     }
279:     /// Creates mock insights for a cycle
280:     private func createMockInsightsForCycle(
281:         _ cycle: Cycle,
282:         profile: UserProfile,
283:         simulationData: [DataPoint],
284:         compoundLibrary: CompoundLibrary
285:     ) -> Insights {
286:         // Extract cycle details
287:         let cycleName = cycle.name
288:         let totalWeeks = cycle.totalWeeks
289:         let stageCount = cycle.stages.count
290:         // Generate summary of compounds and blends used in the cycle
291:         var compoundsUsed = Set<String>()
292:         var blendsUsed = Set<String>()
293:         for stage in cycle.stages {
294:             for compoundItem in stage.compounds {
295:                 if let compound = compoundLibrary.compounds.first(where: { $0.id == compoundItem.compoundID }) {
296:                     compoundsUsed.insert(compound.commonName)
297:                 }
298:             }
299:             for blendItem in stage.blends {
300:                 if let blend = compoundLibrary.blends.first(where: { $0.id == blendItem.blendID }) {
301:                     blendsUsed.insert(blend.name)
302:                 }
303:             }
304:         }
305:         // Get simulation statistics
306:         let maxLevel = simulationData.map { $0.level }.max() ?? 0
307:         let _ = simulationData.map { $0.level }.min() ?? 0
308:         let _ = simulationData.map { $0.level }.reduce(0, +) / Double(max(1, simulationData.count))
309:         // Generate insights for the cycle
310:         var insights = Insights(
311:             title: "Cycle Analysis: \(cycleName)",
312:             summary: "Analysis of your \(totalWeeks)-week cycle with \(stageCount) stages.",
313:             keyPoints: []
314:         )
315:         // 1. Cycle structure point
316:         insights.keyPoints.append(
317:             KeyPoint(
318:                 title: "Cycle Structure",
319:                 description: "Your cycle spans \(totalWeeks) weeks with \(stageCount) distinct stages, using \(compoundsUsed.count) compounds and \(blendsUsed.count) blends.",
320:                 type: .information
321:             )
322:         )
323:         // 2. Compound/blend usage point
324:         if !compoundsUsed.isEmpty || !blendsUsed.isEmpty {
325:             let compoundsList = compoundsUsed.joined(separator: ", ")
326:             let blendsList = blendsUsed.joined(separator: ", ")
327:             var description = "This cycle utilizes "
328:             if !compoundsUsed.isEmpty {
329:                 description += "the following compounds: \(compoundsList)"
330:             }
331:             if !compoundsUsed.isEmpty && !blendsUsed.isEmpty {
332:                 description += " and "
333:             }
334:             if !blendsUsed.isEmpty {
335:                 description += "the following blends: \(blendsList)"
336:             }
337:             insights.keyPoints.append(
338:                 KeyPoint(
339:                     title: "Compounds and Blends",
340:                     description: description,
341:                     type: .information
342:                 )
343:             )
344:         }
345:         // 3. Level analysis point
346:         let targetMax = 1000.0 // Example target maximum
347:         if maxLevel > targetMax * 1.5 {
348:             insights.keyPoints.append(
349:                 KeyPoint(
350:                     title: "Very high peak levels",
351:                     description: "This cycle produces a maximum concentration of \(maxLevel.isFinite ? Int(maxLevel) : 0) ng/dL, which is significantly above the typical target range. Consider reducing dosages during peak periods.",
352:                     type: .warning
353:                 )
354:             )
355:         } else if maxLevel > targetMax {
356:             insights.keyPoints.append(
357:                 KeyPoint(
358:                     title: "Elevated peak levels",
359:                     description: "This cycle produces a maximum concentration of \(maxLevel.isFinite ? Int(maxLevel) : 0) ng/dL, which is above the typical target maximum of \(targetMax.isFinite ? Int(targetMax) : 0) ng/dL.",
360:                     type: .warning
361:                 )
362:             )
363:         }
364:         // 4. Recovery suggestion if appropriate
365:         if totalWeeks > 12 {
366:             insights.keyPoints.append(
367:                 KeyPoint(
368:                     title: "Consider post-cycle recovery",
369:                     description: "Your cycle duration of \(totalWeeks) weeks is relatively long. Consider implementing a proper post-cycle recovery protocol to help restore natural hormone production.",
370:                     type: .suggestion
371:                 )
372:             )
373:         }
374:         return insights
375:     }
376:     /// Creates a mock blend explanation
377:     private func createMockBlendExplanation(_ treatmentProtocol: InjectionProtocol, compoundLibrary: CompoundLibrary) -> String? {
378:         guard treatmentProtocol.protocolType == .blend,
379:               let blendID = treatmentProtocol.blendID,
380:               let blend = compoundLibrary.blends.first(where: { $0.id == blendID }) else {
381:             return nil
382:         }
383:         // Get the components of the blend
384:         let components = blend.resolvedComponents(using: compoundLibrary)
385:         // Return nil if there are no components
386:         if components.isEmpty {
387:             return nil
388:         }
389:         // Create the explanation
390:         var explanation = "\(blend.name) contains \(components.count) different compounds:\n\n"
391:         // Sort components by half-life
392:         let sortedComponents = components.sorted { $0.compound.halfLifeDays < $1.compound.halfLifeDays }
393:         // Add component descriptions
394:         for component in sortedComponents {
395:             let percentage = (component.mgPerML / blend.totalConcentration) * 100
396:             let halfLife = component.compound.halfLifeDays
397:             explanation += "‚Ä¢ \(component.compound.commonName)"
398:             if let ester = component.compound.ester {
399:                 explanation += " \(ester)"
400:             }
401:             explanation += " (\(component.mgPerML.isFinite ? Int(component.mgPerML) : 0) mg/ml, approx. \(percentage.isFinite ? Int(percentage) : 0)%) - "
402:             // Describe the expected behavior
403:             if halfLife < 1.0 {
404:                 explanation += "Very fast-acting with a half-life of \(String(format: "%.1f", halfLife)) days, providing an initial spike in hormone levels.\n"
405:             } else if halfLife < 3.0 {
406:                 explanation += "Fast-acting with a half-life of \(String(format: "%.1f", halfLife)) days, providing relatively quick effects.\n"
407:             } else if halfLife < 7.0 {
408:                 explanation += "Medium-acting with a half-life of \(String(format: "%.1f", halfLife)) days, providing balanced release.\n"
409:             } else {
410:                 explanation += "Long-acting with a half-life of \(String(format: "%.1f", halfLife)) days, providing extended release of hormone levels.\n"
411:             }
412:         }
413:         // Add overall blend characteristics
414:         let shortActingCount = sortedComponents.filter { $0.compound.halfLifeDays < 3.0 }.count
415:         let longActingCount = sortedComponents.filter { $0.compound.halfLifeDays >= 7.0 }.count
416:         explanation += "\nOverall characteristics: "
417:         if shortActingCount > 0 && longActingCount > 0 {
418:             explanation += "This blend is designed to provide both immediate effects (from the shorter-acting compounds) and sustained release (from the longer-acting compounds), creating a balanced hormone profile over time."
419:         } else if shortActingCount > 0 {
420:             explanation += "This blend is primarily designed for quick onset of action, with effects becoming noticeable rapidly after injection."
421:         } else if longActingCount > 0 {
422:             explanation += "This blend is designed for stable, long-term release with minimal fluctuations, requiring less frequent injections."
423:         } else {
424:             explanation += "This blend provides a balanced release profile with moderate onset and duration of action."
425:         }
426:         return explanation
427:     }
