 32:         // Check if profile already exists
 33:         let fetchRequest: NSFetchRequest<CDUserProfile> = CDUserProfile.fetchRequest()
 34:         fetchRequest.predicate = NSPredicate(format: "id == %@", self.id as CVarArg)
