316:         // Check if blend already exists
317:         let fetchRequest: NSFetchRequest<CDVialBlend> = CDVialBlend.fetchRequest()
318:         fetchRequest.predicate = NSPredicate(format: "id == %@", self.id as CVarArg)
319:         var cdBlend: CDVialBlend
