 46:         .sheet(isPresented: $showingAddBloodSheet) {
 47:             AddBloodworkView(injectionProtocol: injectionProtocol)
 48:                 .environmentObject(dataStore)
 49:         }
 50:         .alert("Calibration Updated", isPresented: $showingCalibrateConfirm) {
 51:             Button("OK", role: .cancel) { }
 52:         }
 53:         .alert("Enable Notifications", isPresented: $showingEnableNotificationsAlert) {
