 60:         }
 61:         .sheet(isPresented: $showingNotificationOptions) {
 62:             NotificationSettingsView()
 63:                 .environmentObject(dataStore)
 64:         }
 65:         .onAppear {
 66:             dataStore.selectProtocol(id: injectionProtocol.id)
 67:             dataStore.recalcSimulation()
