 69:             fixProtocolCompound()
 70:         }
 71:     }
 72:     // MARK: - Next Injection View
 73:     var nextInjectionView: some View {
 74:         let nextInjection = nextInjectionDate()
 75:         return VStack(alignment: .leading, spacing: 8) {
 76:             Text("Next Injection")
 77:                 .font(.headline)
 78:             HStack {
